package Strings;

public class MethodCharAt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Method name:charAt();
		
				//Purpose:-It will helps user in getting the character of a string from the user given index position
				
				//Return Type:-character
				
		String str="India";
				
			char c=str.charAt(2);
				System.out.println("The character at position 2 is "+c);
		
		
		
		
		

	}

}
