package StringsExamples;

import java.util.Scanner;

public class PrintEachCharacterOfAString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		
			
		String userval="Testing Masters";
		
		System.out.println(userval.length());
		int strlenth=userval.length()-1;
		
		
		for(int i=0;i<=strlenth;i++)
		{
			
			
			
			
			
			System.out.println("The character from the position "+i+ " --->  is "+userval.charAt(i));
		}
		
		
		
		
		
		
		

	}

}
